import React, {Component} from 'react';
import {Button, FormGroup, FormControl} from 'react-bootstrap';
import {Redirect} from 'react-router-dom';

export default class Login extends Component{
    constructor(props){
        super(props);
        this.state = { username: '', password: '', data:[] };
    }

    handleChange = ({ target }) => {
        this.setState({ [target.name]: target.value });
    };


    validateForm() {
        return this.state.username.length > 0 && this.state.password.length > 0;
    }


    handleSubmit = (event) => {
        this.state.data.map( (user) => {
            if (user.email === this.state.username && user.password === this.state.password)
            {
                <Redirect to="/student" push />
            }
        });
    }

    componentDidMount(){
        fetch('http://localhost:8080/api/users')
            .then(response => response.json())
            .then(data=>{
                console.log(data);
                this.setState({data});
            })
    }

    render() {
        return (
            <React.Fragment>
                <form>
                    <label htmlFor="username">username</label>
                    <input
                        type="text"
                        name="username"
                        value={this.state.username}
                        onChange={this.handleChange}
                    />

                    <label htmlFor="password">password</label>
                    <input
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.handleChange}
                    />
                </form>

                <Button type="submit" disabled={!this.validateForm()} onSubmit={this.handleSubmit}>Login</Button>

                <h3>Your username is: {this.state.username}</h3>
                <h3>Your password is: {this.state.password}</h3>
            </React.Fragment>
        );
    }
}
