import React, {Component} from 'react';
import {Button, FormGroup, FormControl} from 'react-bootstrap';

export default class Login extends Component{
    constructor(props){
        super(props);
        this.state = { username: '', password: '', data:[] };
    }

    handleChange = ({ target }) => {
        this.setState({ [target.name]: target.value });
    };


    validateForm() {
        return this.state.nickname.length > 0 && this.state.password.length > 0;
    }


    handleSubmit = (event, {history}) => {
        this.state.data.map( function (user ) {
            if (user.nick === this.state.nickname && user.password === this.state.password)
                history.push('/student');
        });
    }

    componentDidMount(){
        fetch('http://localhost:8080/api/users')
            .then(response => response.json())
            .then(data=>{
                console.log(data);
                this.setState({data});
            })
    }

    render() {
        return (
            <React.Fragment>
                <form>
                    <label htmlFor="username">username</label>
                    <input
                        type="text"
                        name="username"
                        value={this.state.username}
                        onChange={this.handleChange}
                    />

                    <label htmlFor="password">password</label>
                    <input
                        type="password"
                        name="password"
                        value={this.state.password}
                        onChange={this.handleChange}
                    />
                </form>

                <Button type="submit" disabled={!this.validateForm()} onSubmit={this.handleSubmit}>Login</Button>

                <h3>Your username is: {this.state.username}</h3>
            </React.Fragment>
        );
    }
}
